let selectedOptions = [];

function WhatsappTextCreator(newText) {
  if (selectedOptions.includes(newText)) {
    // Se já estiver no array, remove (toggle off)
    selectedOptions = selectedOptions.filter(item => item !== newText);
  } else {
    // Caso contrário, adiciona (toggle on)
    selectedOptions.push(newText);
  }

  const result = selectedOptions.join(" + ");
  alert(result); // só para ver durante o teste

  return result;
}

export default WhatsappTextCreator;
