import "../../../../styles/courses/math/math.css"
import { Link } from "react-router-dom";
import PriceCalculator from "../../../../PriceCalculator";
import GenerateCourse from "../../../../CoursesGenerator";

//Planets price
export const planets = [
  GenerateCourse({
    id: "conjuntos",
    name: "Conjun"
  }),
  {
    id: "conjuntos-numericos",
    name: "Conjuntos Numéricos",
    price: 30,
    description: "Explora os principais conjuntos numéricos e as operações básicas neles definidas, além de propriedades fundamentais.",
    disponivel: false},
  {
    name: "Operações Básicas e Propriedades",
    path: "operaçoes-basicas-propriedades",
    price: 30,
    description: "Revisão técnica e prática dos algoritmos das quatro operações, fundamentais para cálculos rápidos e acertos em provas.",
    disponivel: false},
  {
    name: "Expressões Numéricas",
    path: "expressoes-numericas",
    price: 30,
    description: "Desenvolve a habilidade de resolver expressões com múltiplas operações, parênteses e regras de precedência.",
    disponivel: false},
  {
    name: "Razão e Proporção",
    path: "razao-proporçao",
    price: 30,
    description: "Essencial para resolver problemas práticos e de lógica em provas, este módulo aborda proporcionalidade, porcentagem e regra de três.",
    disponivel: false},
  {
    name: "Álgebra e Equações",
    path: "algebra-equaçoes",
    price: 30,
    description: "Introduz a linguagem algébrica e resolve equações fundamentais cobradas em diversos concursos.",
    disponivel: false},
  {
    name: "Geometria e Medidas",
    path: "geometria-medidas",
    price: 30,
    description: "Explora a geometria plana e espacial, com foco nas fórmulas e aplicações recorrentes em provas.",
    disponivel: false},
  {
    name: "Estatística",
    path: "estatistica",
    price: 30,
    description: "Ensina como interpretar gráficos, tabelas e calcular média, moda, mediana e frequências — conteúdo frequente em editais.",
    disponivel: false},
  {
    name: "Probabilidade e Análise Combinatória",
    path: "probabilidade-analise-combinatoria",
    price: 30,
    description: "Apresenta técnicas de contagem e cálculo de probabilidades simples, indispensáveis para questões de lógica.",
    disponivel: false},
  {
    name: "Matemática Financeira",
    path: "matematica-financeira",
    price: 30,
    description: "Aplica a matemática a contextos reais de finanças, como juros simples e compostos e porcentagens aplicadas.",
    disponivel: false
  },
]

function GetCoursePrice(courseName){
  const course = planets.find(c => c.name === courseName);
  return course?.price ?? 0;
};


const totalPrice = planets.reduce((sum, planets) => sum + planets.price, 0);;
function MatematicaParaConcursos() {
  return (
    <div className="HomePageGlobalDiv">
      <div className="NousNovaMathPage">
        {/* Título da Página */}
        <section id="title">
          <h1>Provas e Concursos |</h1>
          <h3>O sistema solar dos aprovados</h3>
        </section>

        {/* Introdução */}
        <section>
          <h2>Bem-vindo ao Sistema das Provas e Concursos</h2>
          <p>
            Você está prestes a iniciar uma jornada pelos fundamentos da matemática cobrados nos principais concursos e provas do país.
            Aqui, cada capítulo é um planeta — e cada conceito, um passo em direção ao domínio total da matéria.
            Com aulas diretas, conteúdos claros e foco em aprovação, este curso é seu guia confiável rumo ao sucesso.
            Vamos juntos transformar a matemática em aliada.
            Que o conhecimento te conduza!
          </p>
        </section>

        {/* Planetas (Cursos) */}
        <section>
          <h2>Planetas deste Sistema</h2>
          <div className="planets">
            {planets.map(course => (
              <div className="planet-card" key={course.name}>
                <h3>{course.name}</h3>
                <p>{course.description}</p>
                {course.disponivel ? (
                  <Link to={course.id} className="planet-button" state={{ price: course.price }} target="blank">Saber Mais</Link>
                ) : (
                  <button className="planet-button disabled" disabled> Em breve</button>
                )}
              </div>
            ))}
          </div>
        </section>
        <section id="buy">
          <div id="discount">
            <p id="_2">{PriceCalculator(totalPrice,0)}</p>
            <p id="_1">{PriceCalculator(totalPrice,15)}</p>
          </div>
          <a href="https://www.google.com/" className="card" target="blank">Comprar Todos</a>
        </section>
      </div>
    </div>
  );
}

export default MatematicaParaConcursos;
