import { Link } from "react-router-dom";
import PriceCalculator from "../../../";
//import "../../../../styles/classes/individual/class.css"; // novo CSS específico

function PrivateLessonsLayout({ 
  title, 
  subtitle, 
  introTitle, 
  introText, 
  plans, 
  totalDiscount = 0, 
  checkoutURL 
}) {
  const totalPrice = plans.reduce((sum, plan) => (
    plan.available ? sum + (plan.price - (plan.price * (plan.discount / 100))) : sum
  ), 0);

  return (
    <div className="HomePageGlobalDiv">
      <div className="NousNovaClassPage">
        
        {/* Título */}
        <section id="title">
          <h1>{title} |</h1>
          <h3>{subtitle}</h3>
        </section>

        {/* Introdução */}
        <section>
          <h2>{introTitle}</h2>
          <p>{introText}</p>
        </section>

        {/* Planos */}
        <section>
          <h2>Planos Disponíveis</h2>
          <div className="class-cards">
            {plans.map(plan => (
              <div className="class-card" key={plan.name}>
                <h3>{plan.name}</h3>
                <p>{plan.description}</p>
                {plan.available ? (
                  <Link 
                    to={plan.id} 
                    className="class-button" 
                    state={{ price: plan.price }} 
                    target="_blank"
                  >
                    Ver Detalhes
                  </Link>
                ) : (
                  <button className="class-button disabled" disabled>
                    Em breve
                  </button>
                )}
              </div>
            ))}
          </div>
        </section>

        {/* Área de Compra */}
        <section id="buy">
          {plans.some(plan => plan.available) ? (
            <>
              <div id="discount">
                {totalDiscount !== 0 ? (
                  <>
                    <p id="_2">{PriceCalculator(totalPrice, 0)}</p>
                    <p id="_1">{PriceCalculator(totalPrice, totalDiscount)}</p>
                  </>
                ) : (
                  <p id="_1">{PriceCalculator(totalPrice, totalDiscount)}</p>
                )}
              </div>
              <a href={checkoutURL} className="card" target="_blank">Assinar</a>
            </>
          ) : (
            <button className="class-button disabled" disabled>
              Disponível em breve
            </button>
          )}
        </section>
      </div>
    </div>
  );
}

export default PrivateLessonsLayout;
