import { Link } from "react-router-dom";
import books from "../Books";

function HomePage() {
  return (
    <div className="">
      <h1 className="text-2xl font-bold mb-4">Derwolf Books</h1>
      <p className="mb-6">Escolha um livro para ler ou baixar.</p>

      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-4">
        {books.map((livro) => (
          <div key={livro.id} className="border rounded-xl p-4 shadow hover:shadow-lg transition">
            <h2 className="text-xl font-semibold">{livro.nome}</h2>
            <p className="text-gray-600">{livro.descricao}</p>
            <Link
              to={`/livro/${livro.id}`}
              className="inline-block mt-3 text-blue-600 hover:underline"
            >
              Ler online
            </Link>
          </div>
        ))}
      </div>
    </div>
  );
}

export default HomePage;
