let textArray = [];

function WhatsappTextCreator(actualArray, newText) {
  // Adiciona o novo texto ao array
  actualArray.push(newText);

  // Monta o texto final
  const result = actualArray.join(" "); // Juntando todos os elementos do array em uma string com espaços entre eles

  // Exibe o texto final no console (você pode exibir em uma UI)
  console.log(result);

  return actualArray; // Retorna o array atualizado
}

export default WhatsappTextCreator;
