import "../../../../styles/courses/math/math.css"
import { Link } from "react-router-dom";
import PriceCalculator from "../../../../PriceCalculator";
import GenerateCourse from "../../../../CoursesGenerator";

//Planets
export const planets = [
  //Conjuntos
  GenerateCourse({
    disponivel: fals,
    id: "conjuntos",
    name: "Conjuntos",
    motivation: "Domine a matemática que mais cai e conquiste sua aprovação",
    price: 30,
    description: "Este módulo apresenta os fundamentos da Teoria dos Conjuntos — base para a lógica matemática e muitas questões de concursos.",
    problemTitle: "Problema comum nos concursos",
    problemContent: "Muitos alunos erram questões simples por falta de base em conjuntos.",
    solutionTitle: "Solução completa",
    solutionContent: "Aqui você aprenderá tudo sobre conjuntos com foco em provas.",
    courseContentsTitle: "O que você vai aprender",
    courseContentsContent: [
      "Operações com números naturais, inteiros, racionais e decimais",
      "Problemas com frações, razões e proporções",
      "Porcentagem, juros simples e compostos",
      "Equações do 1º e 2º grau",
      "Análise e interpretação de gráficos e tabelas",
      "Resolução de questões de concursos reais",
    ],
    socialProofTitle: "Resultados de alunos",
    socialProofContent: [
      "image1.png",
      "image2.png",
      "image3.png",
    ],
    freeVideoDemonstrationTitle: "Aula gratuita",
    freeVideoDemonstrationContent: "Veja um trecho da aula para entender como funciona.",
    freeVideoID: "",
    callToActionTitle: "Garanta sua vaga",
    checkOutURL: "",
    FAQTitle: "Dúvidas Frequentes",
    FAQContent: [
      { question: "O curso tem certificado?", answer: "Sim, ao final do curso você receberá um certificado." },
      { question: "Por quanto tempo terei acesso?", answer: "Acesso vitalício ao conteúdo do curso." },
    ],
  }),
  //Conjuntos Numéricos
  GenerateCourse({
    id: "conjuntos-numericos",
    name: "Conjuntos Numéricos",
    motivation: "Construa a base essencial para dominar a matemática que cai em todas as provas.",
    price: 15,
    description:
      "Aprenda o que são conjuntos numéricos, suas classificações e operações básicas. Este é um dos pilares fundamentais para resolver questões matemáticas com confiança.",

    // Landing Page
    problemTitle: "Você sente insegurança ao lidar com os tipos de números nas provas?",
    problemContent:
      "Muitos estudantes confundem os conjuntos numéricos e não sabem como utilizá-los corretamente em problemas práticos. Isso leva a erros bobos que custam pontos preciosos.",
    solutionTitle: "Aqui está a solução",
    solutionContent:
      "Neste curso você aprenderá a identificar e classificar os números em conjuntos (naturais, inteiros, racionais, irracionais e reais), além de revisar as operações básicas com exemplos práticos.",
    courseContentsTitle: "Conteúdo do Curso",
    courseContentsContent: [
      "O que são conjuntos numéricos",
      "Introdução às operações básicas",
    ],
    socialProofTitle: "Quem já estudou recomenda",
    socialProofContent: [
      "aluno1.png",
      "aluno2.png",
      "aluno3.png",
    ],
    freeVideoDemonstrationTitle: "Veja um trecho da aula",
    freeVideoDemonstrationContent: "Assista gratuitamente uma prévia do conteúdo para ter certeza de que este curso é para você.",
    freeVideoID: "", // Substitua pelo ID real do vídeo
    callToActionTitle: "Garanta seu acesso agora mesmo",
    checkOutURL: "", // Substitua pelo UL real do checkout
    FAQTitle: "Perguntas Frequentes",
    FAQContent: [
      {
        question: "Este curso é indicado para iniciantes?",
        answer: "Sim! Ele começa do zero e é ideal para quem precisa revisar ou aprender do início.",
      },
      {
        question: "O acesso é vitalício?",
        answer: "Sim, você poderá rever o conteúdo sempre que quiser.",
      },
      {
        question: "Terei suporte para dúvidas?",
        answer: "Sim, você poderá enviar dúvidas diretamente pela plataforma.",
      },
      {
        question: "Este conteúdo cai em concursos?",
        answer: "Sim! Conjuntos numéricos são um dos temas mais cobrados em provas e concursos.",
      },
      {
        question: "Tem certificado?",
        answer: "Sim, você receberá um certificado ao final do curso.",
      },
    ],
  }),
  //Próximo...
]

const totalPrice = planets.reduce((sum, planet) => (planet.disponivel ? sum + planet.price : sum), 0);
const discount = 10;
function MatematicaParaConcursos() {
  return (
    <div className="HomePageGlobalDiv">
      <div className="NousNovaMathPage">
        {/* Título da Página */}
        <section id="title">
          <h1>Provas e Concursos |</h1>
          <h3>O sistema solar dos aprovados</h3>
        </section>

        {/* Introdução */}
        <section>
          <h2>Bem-vindo ao Sistema das Provas e Concursos</h2>
          <p>
            Você está prestes a iniciar uma jornada pelos fundamentos da matemática cobrados nos principais concursos e provas do país.
            Aqui, cada capítulo é um planeta — e cada conceito, um passo em direção ao domínio total da matéria.
            Com aulas diretas, conteúdos claros e foco em aprovação, este curso é seu guia confiável rumo ao sucesso.
            Vamos juntos transformar a matemática em aliada.
            Que o conhecimento te conduza!
          </p>
        </section>

        {/* Planetas (Cursos) */}
        <section>
          <h2>Planetas deste Sistema</h2>
          <div className="planets">
            {planets.map(course => (
              <div className="planet-card" key={course.name}>
                <h3>{course.name}</h3>
                <p>{course.description}</p>
                {course.disponivel ? (
                  <Link to={course.id} className="planet-button" state={{ price: course.price }} target="blank">Saber Mais</Link>
                ) : (
                  <button className="planet-button disabled" disabled> Em breve</button>
                )}
              </div>
            ))}
          </div>
        </section>
        <section id="buy">
          {planets.map(course => (
            <div key={course.name}>
              {course.disponivel ? (
                <>
                  <div id="discount">
                    {discount != 0 && (
                      <>
                        <p id="_2">{PriceCalculator(totalPrice, 0)}</p>
                        <p id="_1">{PriceCalculator(totalPrice, discount)}</p>
                      </>
                    )}
                    {discount === 0 && (
                      <>
                        <p id="_1">{PriceCalculator(totalPrice, discount)}</p>
                      </>
                    )}
                  </div>
                  <a href="" className="card" target="blank">Comprar Todos</a>
                </>
              ) : (
                null
              )}
            </div>
          ))}
        </section>
      </div>
    </div>
  );
}

export default MatematicaParaConcursos;
