import { useParams } from "react-router-dom";
import book from "../Books";

function BooksPage() {
  const { id } = useParams();
  const books = book.find((l) => l.id === parseInt(id));
  console.log()

  if (!books) return <p>books n√£o encontrado.</p>;

  return (
    <div className="">
      <h1 className="">{books.nome}</h1>
      <p className="">{books.descricao}</p>

      <div className="">
        <iframe
          src={books.arquivo}
          width="100%"
          height="600px"
          title="Leitor PDF"
        />
      </div>

      <a
        href={books.arquivo}
        download
        className=""
      >
        Baixar PDF
      </a>
    </div>
  );
}

export default BooksPage;
