import "../../styles/privateLessons.css";
import { Link } from "react-router-dom";
import PriceCalculator from "../../PriceCalculator"
import { useState } from 'react';
impor

const price_default_Math = 50;
const price_default_Music = 0;
const price_default_Physics = 80;

function PricesPage() {
  // Array para armazenar os textos selecionados
  const [textArray, setTextArray] = useState([]);

  // Função para lidar com a mudança de checkbox
  const handleCheckboxChange = (e) => {
    const { value, checked } = e.target;

    if (checked) {
      setTextArray((prev) => {
        const updated = [...prev, value];
        console.log(updated.join(" "));
        return updated;
      });
    } else {
      setTextArray((prev) => {
        const updated = prev.filter((item) => item !== value);
        console.log(updated.join(" "));
        return updated;
      });
    }
  };

  // Cria o link final
  const whatsappBaseURL = "https://wa.me/5518996181770";

  // Transforma "Disciplina|plano" em objetos
  const planosSelecionados = textArray.map((item) => {
    const [disciplina, plano] = item.split("|");
    return { disciplina, plano };
  });

  // Verifica se há seleções
  const temSelecionado = planosSelecionados.length > 0;

  // Assume que o plano é o mesmo para todas as disciplinas (você pode validar isso se quiser)
  const planoSelecionado = planosSelecionados[0]?.plano || "";

  // Mapeia nomes bonitos
  const nomesDisciplinas = {
    Matematica: "Matemática",
    Musica: "Música",
    Física: "Física",
  };
  const nomesPlanos = {
    plano4: "4 aulas",
    plano8: "8 aulas",
    plano12: "12 aulas",
  };

  // Monta a mensagem se houver seleção
  const mensagemFormatada = temSelecionado
    ? encodeURIComponent(
        `Eu gostaria de assinar o plano: ${nomesPlanos[planoSelecionado]}\nCom as disciplinas:\n` +
        planosSelecionados.map(({ disciplina }) => `- ${nomesDisciplinas[disciplina] || disciplina}`).join("\n")
      )
    : "";

  // Link final do WhatsApp
  const whatsappLink = `${whatsappBaseURL}?text=${mensagemFormatada}`;

  return(
    <div className="HomePageGlobalDiv">
      <div className="NousNovaLessons">
        <section id="title">
          <h1>Consultar Valores</h1>
          <h3>Escolha uma disciplina e descubra como podemos ajudar você a dominar o conhecimento.</h3>
        </section>

        <section>
          <div>
            <h2>Totalmente Personalizado</h2>
              <p>Personalize seus estudos mesclando entre disciplinas</p>
              <div className="prices">
                <div>
                  <h2>Matemática</h2>
                    <ul>
                      <a href="https://mpago.li/2iRvSLP" target="blank">
                        <li>Aula Avulsa: {PriceCalculator(price_default_Math,0)} hora</li>
                      </a>
                      <a href="https://mpago.li/2ytEo4a" target="blank">
                        <li>Aula Experimental: {PriceCalculator(price_default_Math,25)} hora</li>
                      </a>
                    </ul>
                </div>
                <div>
                  <h2>Música</h2>
                    <ul>
                      <a href="" target="blank">
                        <li>Aula Experimental: {PriceCalculator(price_default_Music,0)} hora</li>
                      </a>
                    </ul>
                </div>
                <div>
                  <h2>Física</h2>
                    <ul>
                      <a href="https://mpago.li/13VSfqK" target="blank">
                        <li>Aula Avulsa: {PriceCalculator(price_default_Physics,0)} hora</li>
                      </a>
                      <a href="https://mpago.li/2jqpSHg" target="blank">
                        <li>Aula Experimental: {PriceCalculator(price_default_Physics,25)} hora</li>
                      </a>
                    </ul>
                </div>
                <h2>Planos</h2>
                <div id="subscriptions">
                  <div className="prices">
                    <p>Plano 4 aulas mensais</p>
                    <label>
                      <li><input type="checkbox" value={"Matemematica|plano4"} onChange={handleCheckboxChange}/>Matemática: {PriceCalculator(price_default_Math,30)} hora</li>
                    </label>
                    <label>
                      <li><input type="checkbox" value={"Musica|plano4"} onChange={handleCheckboxChange}/>Música: {PriceCalculator(price_default_Music,0)} hora</li>
                    </label>
                    <label>
                      <li><input type="checkbox" value={"Física|plano4"} onChange={handleCheckboxChange}/>Física: {PriceCalculator(price_default_Physics,30)} hora</li>
                    </label>
                    <a href={whatsappLink} target="_blank" className="card">Solicitar</a>
                  </div>
                  <div className="prices">
                    <p>Plano 8 aulas mensais</p>
                    <label>
                      <li><input type="checkbox" value={"Matemematica|plano8"} onChange={handleCheckboxChange}/>Matemática: {PriceCalculator(price_default_Math,45)} hora</li>
                    </label>
                    <label>
                      <li><input type="checkbox" value={"Musica|plano8"} onChange={handleCheckboxChange}/>Música: {PriceCalculator(price_default_Music,0)} hora</li>
                    </label>
                    <label>
                      <li><input type="checkbox" value={"Física|plano8"} onChange={handleCheckboxChange}/>Física: {PriceCalculator(price_default_Physics,45)} hora</li>
                    </label>
                    <a href={whatsappLink} target="_blank" className="card">Solicitar</a>
                  </div>
                  <div className="prices">
                    <p>Plano 12 aulas mensais</p>
                    <label>
                      <li><input type="checkbox" value={"Matemematica|plano8"} onChange={handleCheckboxChange}/>Matemática: {PriceCalculator(price_default_Math,50)} hora</li>
                    </label>
                    <label>
                      <li><input type="checkbox" value={"Musica|plano8"} onChange={handleCheckboxChange}/>Música: {PriceCalculator(price_default_Music,0)} hora</li>
                    </label>
                    <label>
                      <li><input type="checkbox" value={"Física|plano8"} onChange={handleCheckboxChange}/>Física: {PriceCalculator(price_default_Physics,50)} hora</li>
