import "../styles/teachers.css";
export const teachersArray = [
    {
        name: "Abner Cruz",
        image: "/teachers/AbnerCruz.png",
        disciplines: ["Matemática", "Física"],
    },
    {
        name: "Renata Nóbrega",
        image: "/teachers/RenataNobrega.png",
        disciplines: ["Piano", "Teclado", "Violino", "Violão"],
    },
];

export function TeacherCard({ teacher }) {
  return (
    <div className="teacherCard">
      <img src={teacher.image} alt={teacher.name} className="teacherImage" />
      <h3>{teacher.name}</h3>
      <p>Disciplinas: {teacher.disciplines.join(", ")}</p>
    </div>
  );
}


function Teachers() {
  return (
    <div className="HomePageGlobalDiv">
      <div className="NousNovaTeachers">
        <h2>Professores</h2>
        <div className="teachersGrid">
          {teachersArray.map((teacher, index) => (
            <TeacherCard key={index} teacher={teacher} />
          ))}
        </div>
      </div>
    </div>
  );
}

export default Teachers;
