import "../../styles/privateLessons.css";

function PrivateLessonPage({ 
    title, 
    subtitle, 
    sections, 
    whatsappMsg = "", 
    whatsappButtonText = "Consultar e Agendar"
}) {
    const whatsappLink = `https://api.whatsapp.com/send/?phone=5518996181770&text=${encodeURIComponent(whatsappMsg)}&type=phone_number&app_absent=0`;

    return (
        <div className="HomePageGlobalDiv">
            <div className="NousNovaLessons">
                {/* Título */}
                <section id="title">
                    <h1>{title}</h1>
                    <h3>{subtitle}</h3>
                </section>

                {/* Seções Dinâmicas */}
                {sections.map((section, index) => (
                    <section key={index} id={section.id || ""}>
                        <h2>{section.heading}</h2>
                        {Array.isArray(section.content)
                            ? section.content.map((item, i) =>
                                typeof item === "string" ? (
                                    <p key={i}>{item}</p>
                                ) : (
                                    item.type === "list" && (
                                        <ul key={i}>
                                            {item.items.map((li, j) => (
                                                <li key={j}>{li}</li>
                                            ))}
                                        </ul>
                                    )
                                )
                            )
                            : <p>{section.content}</p>
                        }
                    </section>
                ))}

                {/* Botão final */}
                <section id="button">
                    <a href={whatsappLink} target="blank">
                        <button className="card">{whatsappButtonText}</button>
                    </a>
                </section>
            </div>
        </div>
    );
}

export default PrivateLessonPage;
