import { useParams } from "react-router-dom";
import book from "../Books";

function BooksPage() {
  const { id } = useParams();
  const book = book.find((l) => l.id === parseInt(id));

  if (!book) return <p>book n√£o encontrado.</p>;

  return (
    <div className="p-4">
      <h1 className="text-2xl font-bold">{book.nome}</h1>
      <p className="mb-4">{book.descricao}</p>

      <div className="mb-4">
        <iframe
          src={book.arquivo}
          width="100%"
          height="600px"
          title="Leitor PDF"
        />
      </div>

      <a
        href={book.arquivo}
        download
        className="inline-block bg-blue-600 text-white px-4 py-2 rounded hover:bg-blue-700"
      >
        Baixar PDF
      </a>
    </div>
  );
}

export default BooksPage;
