import { useParams } from "react-router-dom";
import { planets } from "../MatematicaParaConcursos";
import "../../../../../styles/courses/math/planetsLayout.css";

function PlanetPage() {
  const { id } = useParams();
  const planet = planets.find(c => c.id === id);

  if(!planet){
    return <p>Curso n√£o encontrado.</p>
  }

  return(
    <div className="HomePageGlobalDiv">
      <div className="NousNovaPlanetsLayout">
        {/* Header */}
        <section id="title">
          <h1>{planet.name} |</h1>
          <h3>{planet.motivation}</h3>
        </section>

        {/* Visitor Problem */}
        <section>
          <h2>{planet.problem}</h2>
          <p>{planet.problemContent}</p>
        </section>

        {/* Solution */}
        <section>
          <h2>{planet.solutionTitle}</h2>
          <p>{planet.solutionContent}</p>
        </section>

        {/* Course Contents */}
        <section>
          <h2>{planet.courseContentsTitle}</h2>
          <ul>{planet.courseContentsContent.map((item,index)=>(<li key={index}>{item.content}</li>))}</ul>
        </section>

        {/* Social Proof */}
        <section>
          <h2>{planet.solution}</h2>
          <p>{planet.solution_content}</p>
        </section>
      </div>
    </div>
  )
}

export default PlanetPage;
