import "../styles/privateLessons.css";
import { Link } from "react-router-dom";

function PrivateLessons() {

  // Array para armazenar os textos selecionados
  const [textArray, setTextArray] = useState([]);

  // Função para lidar com a mudança de checkbox
  const handleCheckboxChange = (e) => {
    const updatedTextArray = WhatsappTextCreator(textArray, e.target.value);
    setTextArray(updatedTextArray); // Atualiza o estado com o novo array
  };

  return(
    <div className="HomePageGlobalDiv">
      <div className="NousNovaLessons">
        <section id="title">
          <h1>Aulas Particulares</h1>
          <h3>Escolha uma disciplina e descubra como podemos ajudar você a dominar o conhecimento.</h3>
        </section>

        <section className="galaxy-grid">
          <Link to="/nousNova/private-lessons/math" >
            <button className="card">
              <h2>Matemática</h2>
            </button>
          </Link>

          <Link to="/nousNova/private-lessons/physics">
            <button className="card">
              <h2>Física</h2>
            </button>
          </Link>

          <Link to="/nousNova/private-lessons/quimestry">
            <button className="card">
              <h2>Química</h2>
            </button>
          </Link>

          <Link to="/nousNova/private-lessons/music">
            <button className="card">
              <h2>Música</h2>
            </button>
          </Link>
        </section>
      </div>
    </div>
  );
}

export default PrivateLessons;
