import "../../styles/privateLessons.css";
import { useState } from 'react';
import PriceCalculator from "../../PriceCalculator";
import criarWhatsappLink from "../../WhatsappTextCreator";

const DISCIPLINES = [
  { key: 'Matematica', label: 'Matemática', basePrice: 50, link: 'https://wa.me/5518996181770?text=Eu+gostaria+de+agendar+uma+aula+de+matem%C3%A1tica' },
  { key: 'Fisica', label: 'Física', basePrice: 80, link: 'https://wa.me/5518996181770?text=Eu+gostaria+de+agendar+uma+aula+de+f%C3%ADsica' },
  { key: 'Musica', label: 'Música', basePrice: 35, link: 'https://api.whatsapp.com/send/?phone=5518997954628&text=Ol%C3%A1%21+Eu+gostaria+de+agendar+uma+aula+de+música.&type=phone_number&app_absent=0' },
];

const PLANS = [
  { id: 'mensal1x', label: 'Plano Mensal - 1 aula/sem', discount: 5 },
  { id: 'mensal2x', label: 'Plano Mensal - 2 aulas/sem', discount: 10 },
  { id: 'mensal3x', label: 'Plano Mensal - 3 aulas/sem', discount: 15 },
  { id: 'semestral1x', label: 'Plano Semestral - 1 aula/sem', discount: 15 },
  { id: 'semestral2x', label: 'Plano Semestral - 2 aulas/sem', discount: 20 },
  { id: 'semestral3x', label: 'Plano Semestral - 3 aulas/sem', discount: 25 },
  { id: 'anual1x', label: 'Plano Anual - 1 aula/sem', discount: 25 },
  { id: 'anual2x', label: 'Plano Anual - 2 aulas/sem', discount: 30 },
  { id: 'anual3x', label: 'Plano Anual - 3 aulas/sem', discount: 35 },
];

function PricesPage() {
  const [selectedTexts, setSelectedTexts] = useState([]);
  const [currentPlan, setCurrentPlan] = useState(null);
  const whatsappLink = criarWhatsappLink(selectedTexts);

  const handleCheckboxChange = (e) => {
    const { value, checked } = e.target;
    const [discipline, planId] = value.split("|");

    if (planId !== currentPlan) {
      setCurrentPlan(planId);
      setSelectedTexts(checked ? [value] : []);
      clearOtherCheckboxes(planId);
    } else {
      setSelectedTexts(prev =>
        checked ? [...prev, value] : prev.filter(item => item !== value)
      );
    }
  };

  const clearOtherCheckboxes = (planId) => {
    const inputs = document.querySelectorAll("#subscriptions input[type='checkbox']");
    inputs.forEach(input => {
      if (!input.value.includes(planId)) input.checked = false;
    });
  };

  return (
    <div className="HomePageGlobalDiv">
      <div className="NousNovaLessons">
        <header id="title">
          <h1>Consultar Valores</h1>
          <p>Escolha suas disciplinas e descubra como podemos ajudar você a dominar o conhecimento.</p>
        </header>

        <main>
          <section className="disciplines-section">
            <h2>Totalmente Personalizado</h2>
            <p>Personalize seus estudos mesclando entre disciplinas</p>
            <div className="prices">
              {DISCIPLINES.map(d => (
                <article key={d.key} className="discipline-card">
                  <h3>{d.label}</h3>
                  <ul>
                    <li>Aula Avulsa: {PriceCalculator(d.basePrice, 0)} hora</li>
                    <li>Aula Experimental: {PriceCalculator(d.basePrice, 50)} hora</li>
                  </ul>
                  <div id="individualsSolicitation">
                    <a href={d.link} target="_blank" id="individualsSolicitation">
                      <div className="planet-button" id="button">Agendar</div>
                    </a>
                  </div>
                </article>
              ))}
            </div>
          </section>

          <section className="plans-section">
            <h2>Planos</h2>
            <p>Selecione as disciplinas que você quer no plano escolhido</p>
            <div id="subscriptions" className="prices">
              {PLANS.map(plan => (
                <article key={plan.id} className="plan-card">
                  <header>
                    <h3>{plan.label}</h3>
                  </header>
                  <ul>
                    {DISCIPLINES.map(d => (
                      <li key={`${d.key}|${plan.id}`}>                        
                        <label>
                          <input
                            type="checkbox"
                            value={`${d.key}|${plan.id}`}
                            onChange={handleCheckboxChange}
                            className="checkbox"
                          />
                          {d.label}: {PriceCalculator(d.basePrice, plan.discount)} hora
                        </label>
                      </li>
                    ))}
                  </ul>
                  <footer>
                    <a
                      href={selectedTexts.some(text => text.includes(plan.id)) ? whatsappLink : "#"}
                      target="_blank"
                      className="card"
                      id={`planSelection_${plan.id}`}
                      onClick={e => {
                        if (!selectedTexts.some(text => text.includes(plan.id))) {
                          e.preventDefault();
                          const el = document.getElementById(`planSelection_${plan.id}`);
                          el.classList.add("flash-error");
                          setTimeout(() => el.classList.remove("flash-error"), 600);
                        }
                      }}
                    >
                      Contratar
                    </a>
                  </footer>
                </article>
              ))}
            </div>
          </section>
        </main>
      </div>
    </div>
  );
}

export default PricesPage;
