import "../../../styles/courses/math/layout.c";
import { Link } from "react-router-dom";

function Aritmetica() {
  return (
    <div className="HomePageGlobalDiv">
      <div className="NousNovaMath">
        {/* Título da Página */}
        <section id="title">
          <h1>Aritmética |</h1>
          <h3>O sistema solar da base numérica</h3>
        </section>

        {/* Introdução */}
        <section>
          <h2>Bem-vindo ao Sistema Aritmética</h2>
          <p>
            Aqui começa sua jornada matemática. A Aritmética é o berço dos números, o alicerce sobre o qual se ergue o edifício lógico da matemática. Cada planeta representa uma parte essencial do conhecimento aritmético.
          </p>
        </section>

        {/* Planetas (Cursos) */}
        <section>
          <h2>Planetas deste Sistema</h2>
          <div className="planets">
            <div className="planet-card">
              <h3>Planeta 1: Números</h3>
              <p>Conceitos fundamentais, leitura e escrita, operações básicas.</p>
              <Link to="/curso/numeros" className="planet-button">Acessar Curso</Link>
            </div>
            <div className="planet-card">
              <h3>Planeta 2: Conjuntos</h3>
              <p>Ideia de agrupamento, interseções, uniões, subconjuntos e aplicações.</p>
              <Link to="/curso/conjuntos" className="planet-button">Acessar Curso</Link>
            </div>
            {/* Adicione mais planetas conforme necessário */}
          </div>
        </section>
      </div>
    </div>
  );
}

export default Aritmetica;
