import "../../styles/privateLessons.css";
import { Link } from "react-router-dom";
import PriceCalculator from "../../PriceCalculator"
import { useState } from 'react';
import criarWhatsappLink from "../../WhatsappTextCreator";

const price_default_Math = 50;
const price_default_Music = 0;
const price_default_Physics = 80;

function PricesPage() {
  // Array para armazenar os textos selecionados
  const [textArrayPlan4, setTextArrayPlan4] = useState([]);
  const [textArrayPlan8, setTextArrayPlan8] = useState([]);
  const whatsappLink = criarWhatsappLink(textArrayPlan4);

  // Função para lidar com a mudança de checkbox
  const handleCheckboxChange = (e) => {
    const { value, checked } = e.target;
    
    const [disciplina, plano] = value.split("|");

    const updateArray = (arrSetter, currentArray) => {
      if (checked) {
        arrSetter([...currentArray, value]);
      } else {
        arrSetter(currentArray.filter((item) => item !== value));
      }
    };

    if (plano === "plano4") {
      updateArray(setTextArrayPlan4, textArrayPlan4);
    } else if (plano === "plano8") {
      updateArray(setTextArrayPlan8, textArrayPlan8);
    }
  };

  return(
    <div className="HomePageGlobalDiv">
      <div className="NousNovaLessons">
        <section id="title">
          <h1>Consultar Valores</h1>
          <h3>Escolha suas disciplinas e descubra como podemos ajudar você a dominar o conhecimento.</h3>
        </section>

        <section>
          <div>
            <h2>Totalmente Personalizado</h2>
              <p>Personalize seus estudos mesclando entre disciplinas</p>
              <div className="prices">
                <div>
                  <h2>Matemática</h2>
                    <ul>
                      <a href="https://mpago.li/2iRvSLP" target="blank">
                        <li>Aula Avulsa: {PriceCalculator(price_default_Math,0)} hora</li>
                      </a>
                      <a href="https://mpago.li/2ytEo4a" target="blank">
                        <li>Aula Experimental: {PriceCalculator(price_default_Math,25)} hora</li>
                      </a>
                    </ul>
                </div>
                <div>
                  <h2>Música</h2>
                    <ul>
                      <a href="" target="blank">
                        <li>Aula Experimental: {PriceCalculator(price_default_Music,0)} hora</li>
                      </a>
                    </ul>
                </div>
                <div>
                  <h2>Física</h2>
                    <ul>
                      <a href="https://mpago.li/13VSfqK" target="blank">
                        <li>Aula Avulsa: {PriceCalculator(price_default_Physics,0)} hora</li>
                      </a>
                      <a href="https://mpago.li/2jqpSHg" target="blank">
                        <li>Aula Experimental: {PriceCalculator(price_default_Physics,25)} hora</li>
                      </a>
                    </ul>
                </div>
                <h2>Planos</h2>
                <div id="subscriptions">
                  <div className="prices">
                    <p>Plano 4 aulas mensais</p>
                    <label>
                      <li><input type="checkbox" value={"Matemática|plano4"} onChange={handleCheckboxChange} className="checkbox"/>Matemática: {PriceCalculator(price_default_Math,30)} hora</li>
                    </label>
                    <label>
                      <li><input type="checkbox" value={"Música|plano4"} onChange={handleCheckboxChange}/>Música: {PriceCalculator(price_default_Music,0)} hora</li>
                    </label>
                    <label>
                      <li><input type="checkbox" value={"Física|plano4"} onChange={handleCheckboxChange}/>Física: {PriceCalculator(price_default_Physics,30)} hora</li>
                    </label>
                    <a href={textArrayPlan4.length > 0 ? whatsappLink : "#"} target="_blank" className="card" id="planSelection4" onClick={(e) => {const hasPlano4 = textArray4.some((text) => text.includes("plano4")); if (!hasPlano4) {e.preventDefault(); const el = document.getElementById("planSelection4"); el.classList.add("flash-error"); setTimeout(() => {el.classList.remove("flash-error");}, 600);}}}>Solicitar</a>
                  </div>
                  <div className="prices">
                    <p>Plano 8 aulas mensais</p>
                    <label>
                      <li><input type="checkbox" value={"Matemática|plano8"} onChange={handleCheckboxChange}/>Matemática: {PriceCalculator(price_default_Math,45)} hora</li>
                    </label>
                    <label>
                      <li><input type="checkbox" value={"Música|plano8"} onChange={handleCheckboxChange}/>Música: {PriceCalculator(price_default_Music,0)} hora</li>
                    </label>
                    <label>
                      <li><input type="checkbox" value={"Física|plano8"} onChange={handleCheckboxChange}/>Física: {PriceCalculator(price_default_Physics,45)} hora</li>
                    </label>
                    <a href={textArrayPlan4.length > 0 ? whatsappLink : "#"} target="_blank" className="card" id="planSelection4" onClick={(e) => {const hasPlano4 = textArray.some((text) => text.includes("plano4")); if (!hasPlano4) {e.preventDefault(); const el = document.getElementById("planSelection4"); el.classList.add("flash-error"); setTimeout(() => {el.classList.remove("flash-error");}, 600);}}}>Solicitar</a>
                  </div>
                  <div className="prices">
                    <p>Plano 12 aulas mensais</p>
                    <label>
                      <li><input type="checkbox" value={"Matemática|plano8"} onChange={handleCheckboxChange}/>Matemática: {PriceCalculator(price_default_Math,50)} hora</li>
                    </label>
                    <label>
                      <li><input type="checkbox" value={"Música|plano8"} onChange={handleCheckboxChange}/>Música: {PriceCalculator(price_default_Music,0)} hora</li>
                    </label>
                    <label>
                      <li><input type="checkbox" value={"Física|plano8"} onChange={handleCheckboxChange}/>Física: {PriceCalculator(price_default_Physics,50)} hora</li>
                    </label>
                    <a href={textArray.length > 0 ? whatsappLink : "#"} target="_blank" className="card" id="planSelection" onClick={(e) => {const hasPlano4 = textArray.some((text) => text.includes("plano4")); if (!hasPlano4) {e.preventDefault(); const el = document.getElementById("planSelection"); el.classList.add("flash-error"); setTimeout(() => {el.classList.remove("flash-error");}, 600);}}}>Solicitar</a>
                  </div>
                </div>
              </div>
          </div>
        </section>
      </div>
    </div>
  );
}

export default PricesPage;
